if __name__ == "__main__":
    import os, json
    path = os.path.dirname(os.path.abspath(__file__))
    approval, clear, contract = router.compile_program(version=8)
    with open(os.path.join(path, "artifacts/approval.teal"), "w") as f: f.write(approval)
    with open(os.path.join(path, "artifacts/clear.teal"), "w") as f: f.write(clear)
    with open(os.path.join(path, "artifacts/contract.json"), "w") as f: f.write(json.dumps(contract.dictify(), indent=2))
